def productType = context.expand( '${658All#ExProduct}' )
def cover = context.expand( '${658All#ExCover_type}' )

String delimiters = "\\,|\\&";
String[] prodArr = productType.split(delimiters);
StringBuilder Strbld = new StringBuilder()
def payment = context.expand('${658All#Payment type}')
def id = context.expand( '${658#ID}' )
if(prodArr.length > 0)
{	

	for (prod in prodArr)
	{

		if(prod.trim().equals("Roadside"))
		{
			product="Roadside"
			Code="RO"

			if(cover.equals("V1"))
			{
				Productcode = "PRD_UK_0002"
			}
			else
			{
				Productcode = "PRD_UK_0001"
			}
			func_Check(productType,product,"RO",payment,id)
		}

		if(prod.trim().equals("HomeStart")||prod.trim().equals("Home Start"))
		{
			product= "HomeStart"
			func_Check(productType,product,"HS",payment,id)
		}
		if(prod.trim().equals("Relay"))
		{
			product= "Relay"
			func_Check(productType,product,"RE",payment,id)
		}
		if(prod.trim().equals("StayMobile")||prod.trim().equals("Stay Mobile"))
		{
			product= "StayMobile"
			func_Check(productType,product,"SM",payment,id)

		}

	}
	if(context.expand( '${658All#EXBRC}' ).equals("Y"))
	{
		product= "BRC"
		func_Check(productType,product,"BRC",payment,id)

	}
}


def func_Check(productType,product,name,payment,id)
{
	log.info product
	log.info id
	//Transaction price

	def Flex = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:RenewalFlexDetails[1]/tns6:FlexFactor[1]}' )
	def RiskIndicator = context.expand( '${RenewalE2E#Response#declare namespace tns=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; //tns:GenerateQuoteResponse[1]/tns:CommunityOrRiskIndicator[1]}' )


	def CoreNet = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:Product[tns6:ProductPriceName ="'+product+'"]/tns6:PriceList[1]/tns6:Price[1]/tns6:PriceBreakdown[1]/tns6:CorePrice[1]/tns6:NetRate[1]}' )
	def CoreNetTax = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:Product[tns6:ProductPriceName ="'+product+'"]/tns6:PriceList[1]/tns6:Price[1]/tns6:PriceBreakdown[1]/tns6:CorePrice[1]/tns6:NetRateTax[1]}' )
	def CoreComm = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:Product[tns6:ProductPriceName ="'+product+'"]/tns6:PriceList[1]/tns6:Price[1]/tns6:PriceBreakdown[1]/tns6:CorePrice[1]/tns6:Commission[1]}' )
	def CoreCommTax = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:Product[tns6:ProductPriceName ="'+product+'"]/tns6:PriceList[1]/tns6:Price[1]/tns6:PriceBreakdown[1]/tns6:CorePrice[1]/tns6:CommissionTax[1]}' )
	def TaxTotal = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:Product[tns6:ProductPriceName ="'+product+'"]/tns6:PriceList[1]/tns6:Price[1]/tns6:PriceBreakdown[1]/tns6:CorePrice[1]/tns6:TaxTotal[1]}' )
	def CoreTotal = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:Product[tns6:ProductPriceName ="'+product+'"]/tns6:PriceList[1]/tns6:Price[1]/tns6:PriceBreakdown[1]/tns6:CorePrice[1]/tns6:Total[1]}' )
	def AAF = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:Product[tns6:ProductPriceName ="'+product+'"]/tns6:PriceList[1]/tns6:Price[1]/tns6:PriceBreakdown[1]/tns6:ArrangementFee[1]/tns6:Fee[1]}' )
	def AAFTax = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:Product[tns6:ProductPriceName ="'+product+'"]/tns6:PriceList[1]/tns6:Price[1]/tns6:PriceBreakdown[1]/tns6:ArrangementFee[1]/tns6:FeeTax[1]}' )
	def AAFTotal = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:Product[tns6:ProductPriceName ="'+product+'"]/tns6:PriceList[1]/tns6:Price[1]/tns6:PriceBreakdown[1]/tns6:ArrangementFee[1]/tns6:Total[1]}' )

	func_write(payment,"Flex","Flexfactor",Flex) 	
	func_write(payment,"Flex","RiskIndicator",RiskIndicator)	
	func_write(payment,"CORE,Net",product,CoreNet)
	func_write(payment,"CORE,NetTax",product,CoreNetTax)
	func_write(payment,"CORE,Commission",product,CoreComm)
	func_write(payment,"CORE,CommissionTax",product,CoreCommTax)
	func_write(payment,"CORE,TaxTotal",product,TaxTotal)
	func_write(payment,"CORE,Total",product,CoreTotal)
	func_write(payment,"AAF,AAF",product,AAF)
	func_write(payment,"AAF,AAFTax",product,AAFTax)
	func_write(payment,"AAF,Total",product,AAFTotal)

	def response =context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:Product[tns6:ProductPriceName="' + product + '"]/tns6:PriceList[1]/tns6:Price[1]/tns6:PriceBreakdown[1]/tns6:SystemDiscountList[1]/tns6:SystemDiscount[1]/tns6:NetRate[1]}' )

	log.info("response" + response)
 	if( response.equals(''))
 	{

 	}
 	else
