def TotalLessTotalAAFAddons = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:TotalPriceBreakdown[1]/tns6:TotalLessTotalAAFAndTotalAddons[1]}' )
def TotalLessFees = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:TotalPriceBreakdown[1]/tns6:TotalLessSurchargeFees[1]}' )
def TotalLessAAF = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:TotalPriceBreakdown[1]/tns6:TotalLessArrangementFees[1]}' )
def TotalIPT = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:TotalPriceBreakdown[1]/tns6:TotalIPT[1]}' )
def TotalVAT = context.expand( '${RenewalE2E#Response#declare namespace tns8=\'http://www.theaa.com/CommonServices/Message/BS/Road/GenerateQuote_RNL_3\'; declare namespace tns6=\'http://www.theaa.com/CommonServices/Data/Product.2\'; //tns8:GenerateQuoteResponse[1]/tns8:ProductList[1]/tns6:Payment[1]/tns6:TotalPriceBreakdown[1]/tns6:TotalVAT[1]}' )


	func_write(payment,"TotalPriceBreakdown,CombinedTotal","",GrandTotal)
	func_write(payment,"TotalPriceBreakdown,TotalLessDisc","",GrandTotalExcDisc)
	func_write(payment,"TotalPriceBreakdown,DiscountsTotal","",DiscTotal)
	func_write(payment,"TotalPriceBreakdown,BaseCoverTotal","",BaseCoverTotal)
	func_write(payment,"TotalPriceBreakdown,BaseCoverLessDisc","",BaseCoverTotalLessDisc)
	func_write(payment,"TotalPriceBreakdown,BaseCoverAAFTotal","",BaseCoverAAFTotal)
	func_write(payment,"TotalPriceBreakdown,TotalAAF","",TotalAAF)
	func_write(payment,"TotalPriceBreakdown,TotalFee","",FeeTotal)
	func_write(payment,"TotalPriceBreakdown,TotalLessAAFLessAddons","",TotalLessTotalAAFAddons)
	func_write(payment,"TotalPriceBreakdown,TotalLessFee","",TotalLessFees)
	func_write(payment,"TotalPriceBreakdown,TotalLessAAF","",TotalLessAAF)
	func_write(payment,"TotalPriceBreakdown,TotalVAT","",TotalVAT)
	func_write(payment,"TotalPriceBreakdown,TotalIPT","",TotalIPT)
}



def func_write (payment,element,name,RadarValue)
{
	int totalOfValuesChecked = Integer.parseInt(context.expand( '${#TestCase#TotalOfValuesChecked}' ))
	def prodType = context.expand( '${658All#ExProduct}' )

	if (context.expand('${658All#BRC}') == "Y")
	{
		ExCar1 = context.expand( '${658All#ExExCar1}' )
		ExCar2 = context.expand( '${658All#ExCar2}' )
		ExCar3 = context.expand( '${658All#ExCar3}' )
		ExCar4 = context.expand( '${658All#ExCar4}' )
		if (ExCar4 != "")
		{
			product_type = prodType + ",BRC(" + "ExCar1:" + ExCar1 + ",ExCar2:" + ExCar2 + ",ExCar3:" + ExCar3 + ",ExCar4:" + ExCar4 + ")"
		}
		else if (ExCar3 != "")
		{
			product_type = prodType + ",BRC(" + "ExCar1:" + ExCar1 + ",ExCar2:" + ExCar2 + ",ExCar3:" + ExCar3 + ")"
		}
		else if (ExCar2 != "")
		{
			product_type = prodType + ",BRC("  + "ExCar1:" + ExCar1 + ",ExCar2:" + ExCar2 + ")"
		}
		else
		{
			product_type = prodType + ",BRC(" + "ExCar1:" + ExCar1 + ")"
		}
	}
	else
	{
		product_type = context.expand( '${658All#ExProduct}' )
	}


	log.info Pass=(context.expand('${658All#ID}') + "|" + context.expand( '${658All#Renewal Offer}' ) + "|"  + payment + "|" + product_type + "|" + context.expand( '${658All#ExCover_type}' ) + "|" + name + "|" + element + "| " + RadarValue )

	BufferedWriter bw = new BufferedWriter (new FileWriter (context.expand('${#TestCase#RenewalFile}'), true));
	bw.write (Pass);
	bw.newLine();
	bw.flush();
	bw.close();
	totalOfValuesChecked++
	testRunner.testCase.setPropertyValue( 'totalOfValuesChecked',  new Integer(totalOfValuesChecked).toString())
}

